mapper.select:
SELECT name, description, permission FROM (SELECT g.name as name, g.description as description, group_role.role as permission FROM groups g LEFT JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{query.permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, NULL as description, group_role.role as permission FROM group_roles group_role
AND group_role.role=#{query.permission} AND group_role.group_id IS NULL
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups
AND (UPPER(groups.name) LIKE #{query.searchSql} ESCAPE '/')
ORDER BY groups.name
